import React, {Component,  KeyboardEvent}  from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import BookGrid from "./grid/BookGrid";
import BookCard from "./grid/BookCard";
import Search from "./Search";

class UserHome extends Component{
  constructor(props) {
    super(props);
    this.state= {
      user: {},
      myBooks: []

    }
  }
  getUserName(pathname){
    var path_arr = pathname.split("/");
    return path_arr[2];
  }
  getUserBooks(){
    const name = this.getUserName(this.props.location.pathname);
    /*
    axios
      .post("/api/users/getBookList", name)
      .then(res => {
        console.log(res);
      });
    */
    var xhr = new XMLHttpRequest();
    xhr.open("POST", "/api/users/getBookList", true);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.send(JSON.stringify({
      name: name
    }));
    xhr.onload = function() {
      console.log("HELLO")
      console.log(this.responseText);
      var data = JSON.parse(this.responseText);
      console.log(data);
    }
 }
  componentDidMount(){
    //setState({user: decoded_token})
    this.getUserBooks();
  }
  render (){
    const myName = this.getUserName(this.props.location.pathname);
    return (
      <div>
        <div>Hello {myName}</div>
        <div>
          <Link to="/"><button>Explore</button></Link>
        </div>
        <div>
          <BookGrid cols={3}>
          {/*
            this.state.myBooks.map((bookItem, i) => (
              <BookCard title={bookItem.title} id={bookItem.id} author={bookItem.author} img={bookItem.img} description={bookItem.description} key={i}/>
            ))*/}
          </BookGrid>
        </div>
      </div>
    )
  }
};
export default UserHome;
