  state={
    book: ""
  }

//console.log(this.router.props);
  render (){
    const id = getBookID(this.props.location.pathname);
    getBookData(id);


    return (
      <div>
      <h1>Book Page</h1>
      </div>
    )
  }
}
function getBookID(pathname){
  var path_arr = pathname.split("/");
  return path_arr[2];
}
function getBookData(id){
  //console.log(id);
  axios.get('https://www.googleapis.com/books/v1/volumes/' + id)
    .then((response) => {
      //console.log(response.data.volumeInfo);
      const bookItem = {
        title: response.data.volumeInfo.title,
        publisher: response.data.volumeInfo.publisher,
        author: response.data.volumeInfo.author,
        description: response.data.volumeInfo.description,
        publishedDate: response.data.volumeInfo.publishedDate,
        rating: response.data.volumeInfo.publishedDate.averageRating,
      }
      this.setState({book: bookItem});

