class App extends Component {
  state = {
    myBooks: []
      /*
      {
        id: 1,
        title: "Be Here Now",
        author: "Ram Dass",
        img: '/img/BeHereNow.png'
      },
      {
        id: 2,
        title: "Mandalas",
