import React, {Component, KeyboardEvent}  from 'react';
import App from '../App';
import PropTypes from 'prop-types';
//import { Link } from 'react-router-dom'; //add links for pages


class Search extends Component{
//  var searchQuery;
//  const searchQuery;
handleChange = (search) => {
  this.props.searchQuery = search;

}
  hitEnter= (e) => {
    this.props.performSearch(e, this.searchQuery);
  }
  render (){
    return (
      <input type="text" placeholder="Search books.." style={searchStyle} name="searchQuery" onKeyDown={this.hitEnter} onChange={(e) => this.handleChange(e.target.value)}/>
    )
  }
}

const searchStyle = {
  width: '400px',
  float: 'right',
  padding: '6px',
  border: 'none',
  marginTop: '8px',
  marginRight: '16px',
  fontSize: '17px',
  borderRadius: '10px'
}
/*
Search.propTypes = {
  myBooks: PropTypes.array.isRequired
}
*/
export default Search;
