import React, {Component} from 'react';
import BookCard from "./BookCard"
import { Grid, Row, Col } from 'react-flexbox-grid';
import {chunk} from 'lodash';
//import '../../bootstrap/css/bootstrap.min.css'
import './card.css';

//this.props.myBooks = the array of myBooks in the App.js State
type flexColumns = {
  cols: 1 | 2 | 3 | 4 | 6 | 12
}
/*class BookGrid extends Component {
  render() */
  const BookGrid: React.FC<flexColumns> = ({ cols, children}) =>
  {
    const numOfColumns = 3;
    const rows = chunk(this.props.myBooks, numOfColumns);
    return (
      <Grid fluid style={gridStyle}>
      {rows.map((numOfColumns) =>(
        <Row>
          {cols.map((col) => (
            <Col lg={4} md={3}>
              {<BookCard/>}
            </Col>
          ))}
        </Row>
      ))}
      </Grid>
    )
  }





const gridStyle = {
  alignItems: 'center',
  justifyContent: 'center',
  minHeight: '100%',
  minWidth: '100%',
  padding: '40px'
}




export default BookGrid;
