import React, {Component}  from 'react';
import App from '../App';
import PropTypes from 'prop-types';
//import { Link } from 'react-router-dom'; //add links for pages


class Search extends Component{
  render (){
    return (

      <input type="text" placeholder="Search books.." style={searchStyle} value={this.props.searchQuery} onSubmit={this.props.performSearch}/>
    )
  }
}

const searchStyle = {
  width: '400px',
  float: 'right',
  padding: '6px',
  border: 'none',
  marginTop: '8px',
  marginRight: '16px',
  fontSize: '17px',
  borderRadius: '10px'
}
/*
Search.propTypes = {
  myBooks: PropTypes.array.isRequired
}
*/
export default Search;
