import React, {Component} from 'react';
import BookCard from "./BookCard"
import { Grid, Row, Col } from 'react-flexbox-grid';

class BookGrid extends Component {
  render() {
      return (
        <Grid fluid style={gridStyle} >
          <Row style={rowStyle}>
            <Col xs={6} md={3}>
              <BookCard/>
            </Col>
            <Col xs={6} md={3}>
              <BookCard/>
            </Col>
            <Col xs={6} md={3}>
              <BookCard/>
            </Col>
            <Col xs={6} md={3}>
              <BookCard/>
            </Col>
          </Row>
        </Grid>
      )
  }
}

const rowStyle = {
  alignItems: 'center',
  justifyContent: 'center',
  margin: '10px'
}
const gridStyle = {
  alignItems: 'center',
  justifyContent: 'center',
  minHeight: '100%',
  minWidth: '100%'
}



export default BookGrid;
